import React from 'react';

const handleDelete = () => {
    setshowDataLookup([])
    if (selectedValue) {
      setselectedState(selectedValue)
      selectedValue === 'National' ? setstateLevel(false) : setstateLevel(true)
    }
    if (selectedCoords) {
      setselectedPosition(selectedCoords)
    }
  }

const Post = ({id, title, body, author, onDelete }) => (
	<div className="postView">
		<h2>{title}</h2>
		<p>{body}</p>
		<p>Posted by: {author}</p>
		<button onClick={handleDelete()}>Delete</button>
	</div>
)

export default Post;
